/**
 * Central navigation graph for TraceLedger
 * This file owns ALL navigation decisions.
 */
@Composable
fun TraceLedgerNavGraph(
    navController: NavHostController
) {
    NavHost(
        navController = navController,
        startDestination = Routes.DASHBOARD
    ) {

        /* ---------------- DASHBOARD ---------------- */
        composable(Routes.DASHBOARD) {
            DashboardScreen()
        }

        /* ---------------- ACCOUNTS ---------------- */
        composable(Routes.ACCOUNTS) {
            AccountsScreen(
                onAddAccountClick = {
                    navController.navigate(Routes.ADD_ACCOUNT)
                },
                onAccountClick = {
                    // Edit account later (not implemented yet)
                }
            )
        }

        /* ---------------- ADD ACCOUNT ---------------- */
        composable(Routes.ADD_ACCOUNT) {
            AddEditAccountScreen(
                onCancel = {
                    navController.popBackStack()
                },
                onSave = {
                    // Later: persist account to DB
                    navController.popBackStack()
                }
            )
        }

        /* ---------------- TRANSACTIONS LIST ---------------- */
        composable(Routes.TRANSACTIONS) {
            HistoryScreen()
        }

        /* ---------------- ADD TRANSACTION ---------------- */
        composable(Routes.ADD_TRANSACTION) {
            // IMPORTANT:
            // This matches your EXISTING screen signature
            AddTransactionScreen()
        }

        /* ---------------- STATISTICS ---------------- */
        composable(Routes.STATISTICS) {
            StatisticsScreen()
        }
    }
}

