@Composable
fun TraceLedgerBottomBar(
    currentRoute: String?,
    onNavigate: (String) -> Unit,
    onAddTransaction: () -> Unit
) {
    NavigationBar(
        containerColor = Color.Black
    ) {

        NavigationBarItem(
            selected = currentRoute == Screen.Dashboard.route,
            onClick = { onNavigate(Screen.Dashboard.route) },
            icon = { Icon(Icons.Default.Home, null) }
        )

        NavigationBarItem(
            selected = currentRoute == Screen.Accounts.route,
            onClick = { onNavigate(Screen.Accounts.route) },
            icon = { Icon(Icons.Default.AccountBalance, null) }
        )

        // CENTER ADD TRANSACTION (NOT A TAB)
        NavigationBarItem(
            selected = false,
            onClick = onAddTransaction,
            icon = {
                Icon(
                    Icons.Default.Add,
                    contentDescription = "Add Transaction",
                    tint = NothingRed
                )
            },
            alwaysShowLabel = false
        )

        NavigationBarItem(
            selected = currentRoute == Screen.History.route,
            onClick = { onNavigate(Screen.History.route) },
            icon = { Icon(Icons.Default.ReceiptLong, null) }
        )

        NavigationBarItem(
            selected = currentRoute == Screen.Statistics.route,
            onClick = { onNavigate(Screen.Statistics.route) },
            icon = { Icon(Icons.Default.BarChart, null) }
        )
    }
}

