/**
 * Add / Edit Account screen
 * - Uses TopAppBar with Cancel (X) and Save (✓)
 * - Fully scrollable
 * - Credit card fields are OPTIONAL & informational
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AddEditAccountScreen(
    onCancel: () -> Unit,
    onSave: () -> Unit
) {
    // --- UI STATE ---
    var accountType by remember { mutableStateOf(AccountType.BANK) }
    var name by remember { mutableStateOf("") }
    var balance by remember { mutableStateOf("") }
    var details by remember { mutableStateOf("") }
    var includeInTotal by remember { mutableStateOf(true) }
    var selectedColor by remember { mutableStateOf(AccountColors.first()) }

    // Credit card optional fields
    var creditLimit by remember { mutableStateOf("") }
    var billingDay by remember { mutableStateOf("") }
    var dueDay by remember { mutableStateOf("") }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Add Account") },
                navigationIcon = {
                    IconButton(onClick = onCancel) {
                        Icon(Icons.Default.Close, contentDescription = "Cancel")
                    }
                },
                actions = {
                    IconButton(onClick = onSave) {
                        Icon(Icons.Default.Check, contentDescription = "Save")
                    }
                }
            )
        }
    ) { paddingValues ->

        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
                .padding(16.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {

            /* ---------------- ACCOUNT TYPE ---------------- */

            item {
                Text("Account Type", style = MaterialTheme.typography.labelMedium)
            }

            item {
                AccountTypeSelector(
                    selected = accountType,
                    onSelected = { accountType = it }
                )
            }

            /* ---------------- BASIC INFO ---------------- */

            item {
                OutlinedTextField(
                    value = name,
                    onValueChange = { name = it },
                    label = { Text("Account Name") },
                    modifier = Modifier.fillMaxWidth()
                )
            }

            item {
                OutlinedTextField(
                    value = balance,
                    onValueChange = { balance = it },
                    label = { Text("Opening Balance") },
                    modifier = Modifier.fillMaxWidth()
                )
            }

            /* ---------------- INCLUDE IN TOTAL ---------------- */

            item {
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    verticalAlignment = Alignment.CenterVertically,
                    horizontalArrangement = Arrangement.SpaceBetween
                ) {
                    Text("Include in total balance")
                    Switch(
                        checked = includeInTotal,
                        onCheckedChange = { includeInTotal = it }
                    )
                }
            }

            /* ---------------- DETAILS ---------------- */

            item {
                OutlinedTextField(
                    value = details,
                    onValueChange = { details = it },
                    label = { Text("Details (optional)") },
                    modifier = Modifier.fillMaxWidth(),
                    maxLines = 3
                )
            }

            /* ---------------- COLOR SELECTOR ---------------- */

            item {
                Text("Account Color", style = MaterialTheme.typography.labelMedium)
            }

            item {
                LazyRow(horizontalArrangement = Arrangement.spacedBy(12.dp)) {
                    items(AccountColors) { color: Color ->
                        Box(
                            modifier = Modifier
                                .size(36.dp)
                                .clip(CircleShape)
                                .background(color)
                                .border(
                                    width = if (color == selectedColor) 3.dp else 1.dp,
                                    color = if (color == selectedColor)
                                        NothingRed
                                    else
                                        Color.Transparent,
                                    shape = CircleShape
                                )
                                .clickable { selectedColor = color }
                        )
                    }
                }
            }

            /* ---------------- CREDIT CARD OPTIONAL ---------------- */

            if (accountType == AccountType.CREDIT_CARD) {

                item {
                    Text(
                        "Credit Card Details (Optional)",
                        style = MaterialTheme.typography.labelMedium
                    )
                }

                item {
                    OutlinedTextField(
                        value = creditLimit,
                        onValueChange = { creditLimit = it },
                        label = { Text("Total Credit Limit (optional)") },
                        modifier = Modifier.fillMaxWidth()
                    )
                }

                item {
                    Row(horizontalArrangement = Arrangement.spacedBy(12.dp)) {

                        OutlinedTextField(
                            value = billingDay,
                            onValueChange = { billingDay = it },
                            label = { Text("Billing Day (optional)") },
                            modifier = Modifier.weight(1f)
                        )

                        OutlinedTextField(
                            value = dueDay,
                            onValueChange = { dueDay = it },
                            label = { Text("Due Day (optional)") },
                            modifier = Modifier.weight(1f)
                        )
                    }
                }
            }

            // Bottom spacer so content never hides
            item {
                Spacer(modifier = Modifier.height(80.dp))
            }
        }
    }
}

/* -------------------------------------------------------------------------- */
/* ------------------------ ACCOUNT TYPE SELECTOR ---------------------------- */
/* -------------------------------------------------------------------------- */

@Composable
private fun AccountTypeSelector(
    selected: AccountType,
    onSelected: (AccountType) -> Unit
) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .clip(RoundedCornerShape(24.dp))
            .background(MaterialTheme.colorScheme.surfaceVariant)
            .padding(4.dp),
        horizontalArrangement = Arrangement.SpaceBetween
    ) {
        AccountType.values().forEach { type ->
            val isSelected = type == selected

            Text(
                text = type.name.replace("_", " "),
                modifier = Modifier
                    .clip(RoundedCornerShape(20.dp))
                    .background(
                        if (isSelected) NothingRed.copy(alpha = 0.15f)
                        else Color.Transparent
                    )
                    .clickable { onSelected(type) }
                    .padding(horizontal = 14.dp, vertical = 8.dp),
                color = if (isSelected) NothingRed else MaterialTheme.colorScheme.onSurface
            )
        }
    }
}

